css:
@media screen {
		.print-format {
			max-width: 8.27in;
			padding: 0.25in;
			min-height: 11.7in;
        }
        .print-format table tr td {
            padding: 3px 3px !important;
        }
        .print-format table tr th {
            text-align: center !important;
        }
        .borderless td {
            border: 0px !important;
        }
}
@media print {
        .print-format {
            	margin-top: 1mm;
            	margin-left: 5mm;
            	margin-right: 1mm;
        	max-width: 8.27in;
		padding: 5mm;
		max-height: 11.7in;
	}
